import './App.css';
import React, { useState, useEffect } from 'react';
import {StyleSheet, View} from 'react-native';
// ------------------[ Global variables ]------------------ //
// ------------------[ Main method ]------------------ //

const Flex = () => {
  useEffect(() => { document.body.style.backgroundColor = '#000000' }, [])
  const [pokemonProp, setPokemon] = useState(null);

  useEffect(() => {
    GetPokemon("muk").then((object) => setPokemon(object));
  }, []);

  return (
    <View style={styles.index}>
      <PokeInfo pokemon={pokemonProp}/>
      <PokeList/>
    </View>
  );
};

// ------------------[ Info side ]------------------ // 

function PokeInfo({ pokemon }) {
  return pokemon ? (
    <View style={[
      styles.container, 
      {
        flex: 2, 
        marginRight: 10,
        flexDirection: 'column',
      }
    ]}>
      <PokeImage pokemon={pokemon}/>
      <PokeDescription pokemon={pokemon}/>
    </View>
  ) : null;
}

// ------------------[ PokeDesc Components ]------------------ //

function PokeImage({pokemon}) {
  return <View style={[styles.container,{flex: 1, margin: 20}]}>
    <img src={pokemon.sprites.front_default}/>
  </View>
}

function PokeDescription({pokemon}) {
  return <View style={[styles.container,{flex: 2, margin: 20, padding: 10}]} >
    <p>Name: {pokemon.name}</p>
    <p>ID: {pokemon.id}</p>
    <p>Weight: {pokemon.weight}</p>
    <p>Height: {pokemon.height}</p>


  </View>
}

// ------------------[ List side ]------------------ //

const PokeList = () => {
  const [page, setPage] = React.useState(0)

  let pokemon = ShowPokemons(1)

  return (
    <View style={[
      styles.container, 
      {
        flex: 7 
      }
    ]}>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
    </View>
  )
}

// ------------------[ List Components ]------------------ //

function PokeCard(pokemon) {
  return (
    <View style={[styles.container, {flex: 1, borderTopWidth: 1, borderColor: 'black', backgroundColor: 'white', margin: 10, alignItems: 'center'}]}>
      <img/>
      <p>{pokemon.name} tempTest</p>
    </View>
  )
}

// ------------------[ Style Sheets ]------------------ //

const styles = StyleSheet.create({
  index: {
    flex: 1,
    flexDirection: 'row',
    height: 700,
    padding: 20
  },
  container: {
    borderRadius: 10,
    shadowColor: 'black',
    shadowRadius: 4,
    backgroundColor: '#F0F8FF'
  },
  image: {
    margin: 10
  }
})

// ------------------[ Poke API calls ]------------------ //

async function GetPokemon(pokemon) {
  return await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)
                    .then(response => response.json())
}

async function ShowPokemons(page) {

  let offset = page*15

  return await fetch("pokemon?limit=15&offset=0")
          .then(response => response.json())
          .then(data => {
            let results = data.results
            let promiseArray = results.map(obj => {
              console.log(obj)
            })
          })
}

export default Flex;
