import './App.css';
import React, { useState, useEffect } from 'react';
import {StyleSheet, View, Text} from 'react-native';
// ------------------[ Global variables ]------------------ //
// ------------------[ Main method ]------------------ //

const Flex = () => {
  useEffect(() => { document.body.style.backgroundColor = '#000000' }, [])
  const [pokemonProp, setPokemon] = useState(null);

  useEffect(() => {
    GetPokemon("muk").then((object) => setPokemon(object));
  }, []);

  return (
    <View style={styles.index}>
      <PokeInfo pokemon={pokemonProp}/>
      <PokeList/>
    </View>
  );
};

// ------------------[ Info side ]------------------ // 

function PokeInfo({ pokemon }) {
  return pokemon ? (
    <View style={[
      styles.container, 
      {
        flex: 2, 
        marginRight: 10,
        flexDirection: 'column',
      }
    ]}>
      <PokeImage pokemon={pokemon}/>
      <PokeDescription pokemon={pokemon}/>
    </View>
  ) : null;
}

// ------------------[ PokeDesc Components ]------------------ //

function PokeImage({pokemon}) {
  return <View style={[styles.container,{flex: 1, margin: 20}]}>
    <img src={pokemon.sprites.front_default}/>
  </View>
}

function PokeDescription({pokemon}) {
  return <View style={[styles.container,{flex: 2, margin: 20, padding: 10}]} >
    <p>Name: {pokemon.name}</p>
    <p>ID: {pokemon.id}</p>
    <p>Weight: {pokemon.weight}</p>
    <p>Height: {pokemon.height}</p>


  </View>
}

// ------------------[ List side ]------------------ //

const PokeList = () => {
  const [pokemons, setPokemons] = useState([]);

  useEffect(() => {
    ShowPokemons(0).then((data) => setPokemons(data));
  }, []);

  return (
    <View
      style={[
        styles.container,
        {
          flex: 7,
        },
      ]}
    >
      <View style={[{ flexDirection: "row", flex: 1 }]}>
        {pokemons.slice(0, 5).map((pokemon) => (
          <PokeCard key={pokemon.name} pokemon={pokemon} />
        ))}
      </View>
      <View style={[{ flexDirection: "row", flex: 1 }]}>
        {pokemons.slice(5, 10).map((pokemon) => (
          <PokeCard key={pokemon.name} pokemon={pokemon} />
        ))}
      </View>
      <View style={[{ flexDirection: "row", flex: 1 }]}>
        {pokemons.slice(10, 15).map((pokemon) => (
          <PokeCard key={pokemon.name} pokemon={pokemon} />
        ))}
      </View>
    </View>
  );
};

// ------------------[ List Components ]------------------ //

function PokeCard({ pokemon }) {
  return (
    <View
      style={[
        styles.container,
        {
          flex: 1,
          borderTopWidth: 1,
          borderColor: "black",
          backgroundColor: "white",
          margin: 10,
          alignItems: "center",
        },
      ]}
    >
      <img src={pokemon.sprites.front_default}/>
      <Text>{pokemon.name}</Text>
    </View>
  );
}

// ------------------[ Style Sheets ]------------------ //

const styles = StyleSheet.create({
  index: {
    flex: 1,
    flexDirection: 'row',
    height: 700,
    padding: 20
  },
  container: {
    borderRadius: 10,
    shadowColor: 'black',
    shadowRadius: 4,
    backgroundColor: '#F0F8FF'
  },
  image: {
    margin: 10
  }
})

// ------------------[ Poke API calls ]------------------ //

async function GetPokemon(pokemon) {
  return await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)
                    .then(response => response.json())
}

async function ShowPokemons(page) {

  let offset = page*15

  return await fetch(`https://pokeapi.co/api/v2/pokemon?limit=15&offset=${offset}`)
          .then(response => response.json())
          .then(data => {
            let results = data.results
            let promiseArray = results.map( obj => {
              fetch(obj.url)
              .then(response => response.json())
            })
          })
}

export default Flex;
