import './App.css';
import React, { useState, useEffect } from 'react';
import {StyleSheet, View} from 'react-native';
// ------------------[ Global variables ]------------------ //

// ------------------[ Main method ]------------------ //

const Flex = () => {

  const [pokemonProp, setPokemon] = useState(null);

  useEffect(() => {
    GetPokemon("ditto").then((object) => setPokemon(object));
  }, []);

  return (
    <View style={styles.index}>
      <PokeInfo pokemon={pokemonProp}/>
      <PokeList/>
    </View>
  );
};

// ------------------[ Info side ]------------------ // 

function PokeInfo({ pokemon }) {
  return pokemon ? (
    <View style={[
      styles.container, 
      {
        flex: 2, 
        marginRight: 10,
        flexDirection: 'column',
      }
    ]}>
      <PokeImage pokemon={pokemon.sprites.front_default}/>
      <PokeDescription/>
    </View>
  ) : null;
}

// ------------------[ PokeDesc Components ]------------------ //

function PokeImage(img) {
  return <View style={[{flex: 1, margin: 20}]}>
    <img src={img}/>
  </View>
}

function PokeDescription(pokemon) {
  return <View style={[{flex: 2, margin: 20}]} >
    <p>Name: {pokemon.name}</p>
    <p>ID: {pokemon.id}</p>
    <p>Weight: {pokemon.weight}</p>
    <p>Height:</p>


  </View>
}

// ------------------[ List side ]------------------ //

const PokeList = () => {
  const [page, setPage] = React.useState(0)

  let pokemons = ShowPokemons(page)

  return (
    <View style={[
      styles.container, 
      {
        flex: 7 
      }
    ]}>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
      <View style={[{flexDirection: 'row', flex: 1}]}>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
        <PokeCard/>
      </View>
    </View>
  )
}

// ------------------[ List Components ]------------------ //

function PokeCard(pokemon) {
  return (
    <View style={[styles.container, {flex: 1, borderTopWidth: 1, borderColor: 'black', backgroundColor: 'white', margin: 10, alignItems: 'center'}]}>
      <img/>
      <p>{pokemon["name"]} tempTest</p>
    </View>
  )
}

// ------------------[ Style Sheets ]------------------ //

const styles = StyleSheet.create({
  index: {
    flex: 1,
    flexDirection: 'row',
    height: 700,
    padding: 20
  },
  container: {
    borderRadius: 10,
    shadowColor: 'black',
    shadowRadius: 4,
    backgroundColor: '#F0F8FF'
  },
  image: {
    margin: 10
  }
})

// ------------------[ Poke API calls ]------------------ //

async function GetPokemon(pokemon) {
  return await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)
                    .then(response => response.json())
}

async function ShowPokemons(page) {

  let offset = page*15

  let pokemon

  try {
      pokemon = await fetch(`pokemon?limit=15&offset=${offset}`)
          .then(response => response.json())
          .then(data => {
            let results = data.results.map(result => {
              return fetch(result.url)
                    .then(response => response.json())
            })
            return Promise.all(results);
          })
  }   
  catch {
      return "dixk2"
  }

  return pokemon
}

export default Flex;
